package test.day0312;

public class Oper03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int  x = 10; 
		int y = 20; 
		// 조건식 ? true값 : false값; 
		
		System.out.println(x < y ? "크다" : "작다"); //크다  
		
		System.out.println(x += y); // x= x+y 
		System.out.println(x); // 30
		System.out.println(y);  //30 
		
		
		
		
		
	}

}
